import React, { memo } from 'react';

import './seat-selection.css';

const Seat = ({ rowId, position, isFree, onClick }) => (
    <td
        key={position}
        // todo: зачем я оставил здесь инлайн стили?
        style={{
            border: isFree ? '2px solid green' : '2px solid red',
            padding: '15px',
            background: isFree ? 'green' : 'red',
        }}
        onClick={() => onClick(rowId, position, isFree ? false : true)}
    />
);

const Row = ({ id, seats, onClick }) => {
    return (
        <tr key={id}>
            {
                seats.map((seat) => (
                    <Seat
                        key={seat.position}
                        rowId={id}
                        onClick={onClick}
                        {...seat}
                    />
                ))
            }
        </tr>
    )
};

const Seats = ({ seats, onClick }) => {
    return (
        <div className='seat-selection'>

            <div className='prompt'>
                <div className='reserved'>reserved: <td /></div>
                <div className='reservation'>reservation: <td /></div>
            </div>

            <table>
                <tbody>
                    {
                        seats.map((row, i) =>
                            <Row
                                key={i}
                                id={i}
                                seats={row}
                                onClick={onClick}
                            />
                        )
                    }
                </tbody>
            </table>
        </div>
    );
};

export default memo(Seats);
